mapper.insert:
INSERT publish_sign_in (ps_id, tsc_id, s_start_time, s_late_time, s_stop_time, s_check_code)
SELECT #{psId}, tsc_id, #{startTime}, #{lateTime}, #{stopTime}, #{checkCode}
FROM teacher_select_course
WHERE t_id = #{tId}
AND c_id = #{cId}

mapper.select:
SELECT s_check_code AS checkCode
FROM publish_sign_in
WHERE ps_id = #{psId}
