mapper.select:
select
,
from publish_work where pw_id = #{pwId,jdbcType=CHAR}

mapper.insert:
insert into publish_work (pw_id, tsc_id, ct_id, 
  pw_end, pw_state, pw_name
  )
values (#{pwId,jdbcType=CHAR}, #{tscId,jdbcType=INTEGER}, #{ctId,jdbcType=INTEGER}, 
  #{pwEnd,jdbcType=DATE}, #{pwState,jdbcType=BIT}, #{pwName,jdbcType=LONGVARCHAR}
  )

mapper.update:
update publish_work
set tsc_id = #{tscId,jdbcType=INTEGER},
  ct_id = #{ctId,jdbcType=INTEGER},
  pw_end = #{pwEnd,jdbcType=DATE},
  pw_state = #{pwState,jdbcType=BIT}
where pw_id = #{pwId,jdbcType=CHAR}

mapper.select:
		 SELECT COUNT(w.pw_id) AS finishedNum ,pw.*,w.w_score AS wScore,w.s_w_state AS wState,pw.pw_img_src AS activityImgSrc
        FROM WORK w,publish_work pw
		WHERE
		w.s_id IN(SELECT DISTINCT w.s_id
				FROM student s,student_select_course ssc ,teacher_select_course tsc ,course c,publish_work pw,WORK w
				WHERE 
				ssc.tsc_id=tsc.tsc_id
				AND tsc.c_id=c.c_id
				AND pw.tsc_id=tsc.tsc_id
				AND pw.pw_id=w.pw_id
				AND s.s_id=#{sId}
				AND c.c_id=#{cId}
				
					AND pw.pw_state=#{pwState}
				)
		AND pw.pw_id=w.pw_id
		
			AND pw.pw_state=#{pwState}
		
		GROUP BY w.pw_id
		LIMIT #{start}, #{end}

SELECT COUNT(w.pw_id) AS finishedNum ,pw.*,w.w_score AS wScore,w.s_w_state AS wState,pw.pw_img_src AS activityImgSrc
FROM WORK w,publish_work pw
WHERE
w.s_id IN(SELECT DISTINCT w.s_id
FROM student s,student_select_course ssc ,teacher_select_course tsc ,course c,publish_work pw,WORK w
WHERE 
ssc.tsc_id=tsc.tsc_id
AND tsc.c_id=c.c_id
AND pw.tsc_id=tsc.tsc_id
AND pw.pw_id=w.pw_id
AND tsc.t_id=#{tId}
AND c.c_id=#{cId}

	AND pw.pw_state=#{pwState}

)
AND pw.pw_id=w.pw_id
AND w.s_w_state=1

	AND pw.pw_state=#{pwState}

GROUP BY w.pw_id

	AND pw.pw_state=#{pwState}

LIMIT #{start}, #{end}

mapper.update:
	 update publish_work
	 set
	 pw_state = #{pwState}
     where pw_id = #{pwId}

mapper.select:
SELECT DISTINCT  tf.t_f_add AS url,tf.t_f_name AS name ,tf.t_f_id AS tfId,tf.t_f_type AS TYPE,tf.t_f_state AS state
FROM publish_work pw,student_select_course ssc,t_file tf,img_type it
WHERE
ssc.s_id=#{sId}
AND pw.pw_id = #{pwId}
AND ssc.tsc_id=pw.tsc_id
AND pw.pw_id=tf.pw_id
AND tf.t_f_type NOT IN (
	SELECT img_type.i_t_typt FROM img_type
)

SELECT DISTINCT  tf.t_f_add AS url,tf.t_f_name AS name ,tf.t_f_id AS tfId,tf.t_f_type AS type,tf.t_f_state AS state
FROM publish_work pw,student_select_course ssc,t_file tf,img_type it
WHERE
ssc.s_id=#{sId}
AND pw.pw_id = #{pwId}
AND ssc.tsc_id=pw.tsc_id
AND pw.pw_id=tf.pw_id
AND tf.t_f_type IN (
	SELECT img_type.i_t_typt FROM img_type
)

SELECT	pw.pw_img_src AS activityImgSrc,pw.pw_name AS pwName,pw.pw_content AS pwContent,pw.pw_end pwEnd,pw.pw_score pwScore,pw.pw_state as pwState
FROM publish_work pw
WHERE pw.pw_id=#{pwId}

SELECT	w.w_context AS wContext,w.w_problem AS wProblem,w.w_score AS wScore,w.w_remark wRemark,w.s_w_state AS swState,w.t_w_state AS twState,w.w_time AS wTime,w.w_id AS wId
FROM publish_work pw,WORK w
WHERE pw.pw_id=#{pwId}
AND pw.pw_id=w.pw_id
AND w.s_id=#{sId}
