mapper.select:
SELECT COUNT(*)
FROM student_select_course
WHERE student_select_course.s_id = #{sId}
AND student_select_course.ct_id = #{cId}
LIMIT 1

SELECT COUNT(*)
FROM student_select_course, teacher_select_course
WHERE student_select_course.ct_id = #{cId}
OR (teacher_select_course.c_id = #{cId}
AND teacher_select_course.tsc_id = student_select_course.tsc_id)

SELECT COUNT(*)
	FROM student_select_course
	where
	
		student_select_course.tsc_id=#{tscId} 
	
	
		student_select_course.ct_id=#{ctId}
