mapper.select:
		SELECT * FROM TEACHER 
 		WHERE T_ID =#{username,jdbcType=VARCHAR}
 		AND T_PASSWORD =#{password,jdbcType=VARCHAR}

SELECT t_password
FROM teacher
WHERE t_id = #{id}

mapper.update:
	UPDATE TEACHER 
	SET T_PASSWORD =#{newPassword,jdbcType=VARCHAR}
    WHERE T_ID=#{username,jdbcType=VARCHAR}

update teacher
set t_img_src=#{tImgSrc}
where t_id=#{tId}
